sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
  var frame = document.getElementById('kc_frame');

	var ifr = document.createElement("IFRAME");
     // ifr.setAttribute( "src", 'https://integration.rightanswers.com/' );
	ifr.setAttribute( "src", 'https://www.youtube.com/embed/Jf-hbOou_jo' );
ifr.setAttribute( "width", c.width );
      ifr.setAttribute( "height", c.height );
      ifr.setAttribute( "align", c.align );
      ifr.setAttribute( "frameBorder", c.border );
      ifr.setAttribute( "marginHeight", "0" );
      ifr.setAttribute( "marginWidth", "0" );
      ifr.setAttribute( "scrolling", c.scrolling );
      ifr.setAttribute( "id", "mainIframe" );
	document.getElementById('kc_frame').appendChild(ifr);

}
